CREATE FUNCTION public.get_template_statistics(p_tenant_id character varying) RETURNS TABLE(template_id integer, template_name character varying, template_type character varying, specialty character varying, usage_count bigint, unique_users bigint, avg_completion_time numeric, last_used timestamp without time zone, is_popular boolean)
    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  SELECT 
    t.id as template_id,
    t.name as template_name,
    t.template_type,
    t.specialty,
    COUNT(u.id) as usage_count,
    COUNT(DISTINCT u.user_id) as unique_users,
    AVG(u.completion_time_seconds) as avg_completion_time,
    MAX(u.used_at) as last_used,
    COUNT(u.id) > 10 as is_popular -- Popular if used more than 10 times
  FROM medical_record_templates t
  LEFT JOIN medical_record_template_usage u ON t.id = u.template_id
  WHERE t.tenant_id = p_tenant_id AND t.is_active = true
  GROUP BY t.id, t.name, t.template_type, t.specialty
  ORDER BY usage_count DESC, t.name;
END;
$$;


