--
-- Name: attendance_analytics; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.attendance_analytics AS
 SELECT date_trunc('month'::text, (sa.attendance_date)::timestamp with time zone) AS month,
    count(*) AS total_records,
    count(
        CASE
            WHEN ((sa.status)::text = 'present'::text) THEN 1
            ELSE NULL::integer
        END) AS present_count,
    count(
        CASE
            WHEN ((sa.status)::text = 'absent'::text) THEN 1
            ELSE NULL::integer
        END) AS absent_count,
    count(
        CASE
            WHEN ((sa.status)::text = 'late'::text) THEN 1
            ELSE NULL::integer
        END) AS late_count,
    count(
        CASE
            WHEN ((sa.status)::text = 'half_day'::text) THEN 1
            ELSE NULL::integer
        END) AS half_day_count,
    count(
        CASE
            WHEN ((sa.status)::text = 'leave'::text) THEN 1
            ELSE NULL::integer
        END) AS leave_count,
    round((((count(
        CASE
            WHEN ((sa.status)::text = 'present'::text) THEN 1
            ELSE NULL::integer
        END))::numeric / (NULLIF(count(*), 0))::numeric) * (100)::numeric), 2) AS attendance_rate,
    count(
        CASE
            WHEN ((sa.leave_type)::text = 'sick'::text) THEN 1
            ELSE NULL::integer
        END) AS sick_leave_count,
    count(
        CASE
            WHEN ((sa.leave_type)::text = 'vacation'::text) THEN 1
            ELSE NULL::integer
        END) AS vacation_count
   FROM public.staff_attendance sa
  GROUP BY (date_trunc('month'::text, (sa.attendance_date)::timestamp with time zone));


