    sc.credential_name,
    sc.expiry_date,
    sc.status,
        CASE
            WHEN (sc.expiry_date < CURRENT_DATE) THEN 'expired'::text
            WHEN (sc.expiry_date <= (CURRENT_DATE + '30 days'::interval)) THEN 'expiring_soon'::text
            WHEN (sc.expiry_date <= (CURRENT_DATE + '90 days'::interval)) THEN 'expiring_in_90_days'::text
