CREATE VIEW public.dashboard_analytics AS
 SELECT count(DISTINCT sp.id) AS total_staff,
    count(DISTINCT
        CASE
            WHEN ((sp.status)::text = 'active'::text) THEN sp.id
            ELSE NULL::integer
        END) AS active_staff,
    count(DISTINCT
        CASE
            WHEN ((sp.status)::text = 'on_leave'::text) THEN sp.id
