        END) AS scheduled_shifts,
    count(DISTINCT
        CASE
            WHEN ((ss.status)::text = 'completed'::text) THEN ss.id
            ELSE NULL::integer
        END) AS completed_shifts,
    count(DISTINCT sa.id) AS total_attendance_records,
    count(DISTINCT
        CASE
            WHEN ((sa.status)::text = 'present'::text) THEN sa.id
            ELSE NULL::integer
        END) AS present_count,
    count(DISTINCT
        CASE
            WHEN ((sa.status)::text = 'absent'::text) THEN sa.id
            ELSE NULL::integer
        END) AS absent_count,
    avg(
        CASE
            WHEN (sper.performance_score IS NOT NULL) THEN sper.performance_score
            ELSE NULL::numeric
        END) AS avg_performance_score
   FROM (((public.staff_profiles sp
     LEFT JOIN public.staff_schedules ss ON ((sp.id = ss.staff_id)))
     LEFT JOIN public.staff_attendance sa ON ((sp.id = sa.staff_id)))
     LEFT JOIN public.staff_performance sper ON ((sp.id = sper.staff_id)));


