--
-- Name: file_access_patterns; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.file_access_patterns AS
 SELECT file_access_logs.tenant_id,
    file_access_logs.file_id,
    file_access_logs.file_path,
    count(*) AS total_accesses,
    count(DISTINCT file_access_logs.user_id) AS unique_users,
    max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) AS last_accessed,
    min(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) AS first_accessed,
    avg(file_access_logs.file_size_bytes) AS avg_file_size,
    count(
        CASE
            WHEN ((file_access_logs.access_type)::text = 'download'::text) THEN 1
            ELSE NULL::integer
        END) AS download_count,
    count(
        CASE
            WHEN ((file_access_logs.access_type)::text = 'view'::text) THEN 1
            ELSE NULL::integer
        END) AS view_count,
    count(
        CASE
            WHEN (file_access_logs.success = false) THEN 1
            ELSE NULL::integer
        END) AS error_count,
    avg(file_access_logs.response_time_ms) AS avg_response_time,
        CASE
            WHEN (max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) = min(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at))) THEN (count(*))::numeric
            ELSE ((count(*))::numeric / GREATEST((1)::numeric, EXTRACT(days FROM (max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) - min(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at))))))
        END AS accesses_per_day,
        CASE
            WHEN (max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) < (now() - '180 days'::interval)) THEN 'DEEP_ARCHIVE'::text
            WHEN (max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) < (now() - '90 days'::interval)) THEN 'GLACIER'::text
            WHEN (max(COALESCE(file_access_logs.access_time, file_access_logs.accessed_at)) < (now() - '30 days'::interval)) THEN 'STANDARD_IA'::text
            ELSE 'STANDARD'::text
        END AS recommended_storage_class
   FROM public.file_access_logs
  GROUP BY file_access_logs.tenant_id, file_access_logs.file_id, file_access_logs.file_path;


