--
-- Name: payroll_analytics; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.payroll_analytics AS
 SELECT date_trunc('month'::text, (spay.pay_period_start)::timestamp with time zone) AS month,
    count(*) AS total_payroll_records,
    sum(spay.base_salary) AS total_base_salary,
    sum(spay.overtime_pay) AS total_overtime_pay,
    sum(spay.bonuses) AS total_bonuses,
    sum(spay.deductions) AS total_deductions,
    sum(spay.net_pay) AS total_net_pay,
    avg(spay.net_pay) AS avg_net_pay,
    sum(spay.overtime_hours) AS total_overtime_hours,
    count(
        CASE
            WHEN ((spay.payment_status)::text = 'paid'::text) THEN 1
            ELSE NULL::integer
        END) AS paid_count,
    count(
        CASE
            WHEN ((spay.payment_status)::text = 'pending'::text) THEN 1
            ELSE NULL::integer
        END) AS pending_count
   FROM public.staff_payroll spay
  GROUP BY (date_trunc('month'::text, (spay.pay_period_start)::timestamp with time zone));


--
-- Name: performance_analytics; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.performance_analytics AS
 SELECT date_trunc('quarter'::text, (sper.review_date)::timestamp with time zone) AS quarter,
    count(*) AS total_reviews,
    avg(sper.performance_score) AS avg_score,
    min(sper.performance_score) AS min_score,
    max(sper.performance_score) AS max_score,
    count(
        CASE
            WHEN (sper.performance_score >= 4.0) THEN 1
            ELSE NULL::integer
        END) AS excellent_count,
    count(
        CASE
            WHEN ((sper.performance_score >= 3.0) AND (sper.performance_score < 4.0)) THEN 1
            ELSE NULL::integer
        END) AS good_count,
    count(
        CASE
            WHEN (sper.performance_score < 3.0) THEN 1
            ELSE NULL::integer
        END) AS needs_improvement_count
   FROM public.staff_performance sper
  GROUP BY (date_trunc('quarter'::text, (sper.review_date)::timestamp with time zone));


