-- Name: schedule_analytics; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.schedule_analytics AS
 SELECT date_trunc('week'::text, (ss.shift_date)::timestamp with time zone) AS week,
    count(*) AS total_shifts,
    count(
        CASE
            WHEN ((ss.shift_type)::text = 'morning'::text) THEN 1
            ELSE NULL::integer
        END) AS morning_shifts,
    count(
        CASE
            WHEN ((ss.shift_type)::text = 'afternoon'::text) THEN 1
            ELSE NULL::integer
        END) AS afternoon_shifts,
    count(
        CASE
            WHEN ((ss.shift_type)::text = 'night'::text) THEN 1
            ELSE NULL::integer
        END) AS night_shifts,
    count(
        CASE
            WHEN ((ss.shift_type)::text = 'on-call'::text) THEN 1
            ELSE NULL::integer
        END) AS on_call_shifts,
    count(
        CASE
            WHEN ((ss.status)::text = 'completed'::text) THEN 1
            ELSE NULL::integer
        END) AS completed_shifts,
    count(
        CASE
            WHEN ((ss.status)::text = 'cancelled'::text) THEN 1
            ELSE NULL::integer
        END) AS cancelled_shifts,
    avg((EXTRACT(epoch FROM (ss.shift_end - ss.shift_start)) / (3600)::numeric)) AS avg_shift_hours
   FROM public.staff_schedules ss
  GROUP BY (date_trunc('week'::text, (ss.shift_date)::timestamp with time zone));


